~@angular/material/theming
;



// 定义一个接受主题并输出组件颜色样式的mixin。


@mixin
 candy-carousel-theme
(
$theme
)

{


// 定义一个接受主题并输出组件颜色样式的mixin。


$primary
:

map-get
(
$theme
,

primary
);


$accent
:

map-get
(
$theme
,

accent
);



// 定义一个接受主题并输出组件颜色样式的mixin。


.candy-carousel

{


background-color
:

mat-color
(
$
primary
);


border-color
:

mat-color
(
$
accent
,

A400
);


}


}





